import{_ as a,c as t,o as n,aq as e}from"./chunks/framework.ODH9prVs.js";const u=JSON.parse('{"title":"EditorConfig配置","description":"","frontmatter":{},"headers":[],"relativePath":"工具/editorConfig.md","filePath":"工具/editorConfig.md","lastUpdated":1745040158000}'),i={name:"工具/editorConfig.md"},s=e(`<h1 id="editorconfig配置" tabindex="-1">EditorConfig配置 <a class="header-anchor" href="#editorconfig配置" aria-label="Permalink to &quot;EditorConfig配置&quot;">​</a></h1><h2 id="一、什么是-editorconfig" tabindex="-1">一、什么是 EditorConfig <a class="header-anchor" href="#一、什么是-editorconfig" aria-label="Permalink to &quot;一、什么是 EditorConfig&quot;">​</a></h2><p>EditorConfig 有助于为跨各种编辑器和 IDE 处理同一项目的多个开发人员保持一致的编码风格，它包含用于定义编码样式的文件格式和一组文本编辑器插件，使编辑器能够读取文件格式并遵守定义的样式。</p><h2 id="二、通配符模式" tabindex="-1">二、通配符模式 <a class="header-anchor" href="#二、通配符模式" aria-label="Permalink to &quot;二、通配符模式&quot;">​</a></h2><table><thead><tr><th>*</th><th>匹配任何字符串，路径分隔符 ( /)除外</th></tr></thead><tbody><tr><td>**</td><td>匹配任意字符串辅导</td></tr><tr><td>?</td><td>匹配任何单个字符</td></tr><tr><td><code>[name]</code></td><td>匹配名称中的任何单个字符</td></tr><tr><td><code>[!name]</code></td><td>匹配任何不在名称中的单个字符</td></tr><tr><td><code>{s1,s2,s3}</code></td><td>匹配给定的任何字符串（以逗号分隔）（自 EditorConfig Core 0.11.0 起可用）</td></tr><tr><td><code> {num1..num2}</code></td><td>匹配num1和num2之间的任何整数，其中 num1 和 num2 可以是正数或负数</td></tr></tbody></table><h2 id="三、属性" tabindex="-1">三、属性 <a class="header-anchor" href="#三、属性" aria-label="Permalink to &quot;三、属性&quot;">​</a></h2><ul><li>root：应在文件顶部任何部分之外指定的特殊属性。设置为true以停止.editorconfig对当前文件的文件搜索</li><li>indent_style：设置为<strong>制表符</strong>或<strong>空格</strong>分别使用硬制表符或软制表符</li><li>indent_size：一个整数，定义用于每个缩进级别的列数和软选项卡的宽度（如果支持）。当设置为<code>tab</code>tab_width时，将使用（如果指定）的值</li><li>tab_width：一个整数，定义用于表示制表符的列数。这默认为 的值，indent_size通常不需要指定</li><li>end_of_line：设置为lf、cr或crlf以控制换行符的表示方式</li><li>charset：设置为latin1 , utf-8 , utf-8-bom , utf-16be或utf-16le来控制字符集</li><li>trim_trailing_whitespace：设置为true以删除换行符之前的任何空白字符，设置为false以确保不会</li><li>insert_final_newline： 设置为true以确保文件在保存时以换行符结尾，设置为false以确保它不会</li></ul><h2 id="四、实例" tabindex="-1">四、实例 <a class="header-anchor" href="#四、实例" aria-label="Permalink to &quot;四、实例&quot;">​</a></h2><h4 id="一般前端工程的配置实例" tabindex="-1">一般前端工程的配置实例 <a class="header-anchor" href="#一般前端工程的配置实例" aria-label="Permalink to &quot;一般前端工程的配置实例&quot;">​</a></h4><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>root = true</span></span>
<span class="line"><span></span></span>
<span class="line"><span>[*]</span></span>
<span class="line"><span>charset = utf-8</span></span>
<span class="line"><span>indent_style = space</span></span>
<span class="line"><span>indent_size = 2</span></span>
<span class="line"><span>insert_final_newline = true</span></span>
<span class="line"><span>trim_trailing_whitespace = true</span></span>
<span class="line"><span>end_of_line = lf</span></span>
<span class="line"><span>[*.md]</span></span>
<span class="line"><span>trim_trailing_whitespace = false</span></span></code></pre></div>`,10),o=[s];function r(d,l,c,p,h,_){return n(),t("div",null,o)}const g=a(i,[["render",r]]);export{u as __pageData,g as default};
